'use strict';

var util = require('util');
var ResourceBase = require('./resourceBase');

var Episodes = function(config) {
    ResourceBase.call(this, 'episodes', config);
};

util.inherits(Episodes, ResourceBase);

(function() {

    this.retrieve = function(id, callback) {
        return this._transmit('GET', id, null, null, callback);
    };

    this.images = function(id, params, callback) {
        return this._transmit('GET', id + '/images', params, null, callback);
    };

}).call(Episodes.prototype);

module.exports = Episodes;
