'use strict';

var util = require('util');
var ResourceBase = require('./resourceBase');

var Genres = function(config) {
    ResourceBase.call(this, 'genres', config);
};

util.inherits(Genres, ResourceBase);

(function() {

    this.list = function(params, callback) {
        return this._transmit('GET', null, params, null, callback);
    };

}).call(Genres.prototype);

module.exports = Genres;
