'use strict';

var util = require('util');
var ResourceBase = require('./resourceBase');

var Movies = function(config) {
    ResourceBase.call(this, 'movies', config);
};

util.inherits(Movies, ResourceBase);

(function() {

    this.list = function(params, callback) {
        return this._transmit('GET', null, params, null, callback);
    };

    this.retrieve = function(id, callback) {
        return this._transmit('GET', id, null, null, callback);
    };

    this.images = function(id, params, callback) {
        return this._transmit('GET', id + '/images', params, null, callback);
    };

    this.related = function(id, params, callback) {
        return this._transmit('GET', id + '/related', params, null, callback);
    };

    this.trailers = function(id, params, callback) {
        return this._transmit('GET', id + '/videos', params, null, callback);
    };

}).call(Movies.prototype);

module.exports = Movies;
