'use strict';

var util = require('util');
var ResourceBase = require('./resourceBase');

var Person = function(config) {
    ResourceBase.call(this, 'person', config);
};

util.inherits(Person, ResourceBase);

(function() {


    this.retrieve = function(id, callback) {
        return this._transmit('GET', id, null, null, callback);
    };

    this.images = function(id, params, callback) {
        return this._transmit('GET', id + '/images', params, null, callback);
    };

    this.credits = function(id, params, callback) {
        return this._transmit('GET', id + '/credits', params, null, callback);
    };

}).call(Person.prototype);

module.exports = Person;
