'use strict';

var util = require('util');
var ResourceBase = require('./resourceBase');

var Regions = function(config) {
    ResourceBase.call(this, 'regions', config);
};

util.inherits(Regions, ResourceBase);

(function() {

    this.list = function(params, callback) {
        return this._transmit('GET', null, params, null, callback);
    };

}).call(Regions.prototype);

module.exports = Regions;
