'use strict';

var util = require('util');
var ResourceBase = require('./resourceBase');

var Search = function(config) {
    ResourceBase.call(this, 'search', config);
};

util.inherits(Search, ResourceBase);

(function() {

    this.movies = function(params, callback) {
        params['type'] = 'movie';
        return this._transmit('GET', null, params, null, callback);
    };

    this.shows = function(params, callback) {
        params['type'] = 'show';
        return this._transmit('GET', null, params, null, callback);
    };

    this.person = function(params, callback) {
        params['type'] = 'person';
        return this._transmit('GET', null, params, null, callback);
    };

    this.channels = function(params, callback) {
        params['type'] = 'channel';
        return this._transmit('GET', null, params, null, callback);
    };

}).call(Search.prototype);

module.exports = Search;
