'use strict';

var util = require('util');
var ResourceBase = require('./resourceBase');

var Updates = function(config) {
    ResourceBase.call(this, 'updates', config);
};

util.inherits(Updates, ResourceBase);

(function() {

    this.list = function(params, callback) {
        return this._transmit('GET', null, params, null, callback);
    };

}).call(Updates.prototype);

module.exports = Updates;
